import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import {
  Trash2,
  GripVertical,
  ChevronDown,
  ChevronUp,
  Plus,
  X,
  AlertCircle
} from 'lucide-react';

const FieldEditor = ({ field, onUpdate, onRemove }) => {
  const [isExpanded, setIsExpanded] = useState(true);

  if (!field) return null;

  const handleLabelChange = (e) => {
    const label = e.target.value;
    const name = label.toLowerCase().replace(/\s+/g, '_').replace(/[^a-z0-9_]/g, '');
    onUpdate({ label, name });
  };

  const handleOptionChange = (optionId, key, value) => {
    const newOptions = field.options.map(opt =>
      opt.id === optionId ? { ...opt, [key]: value } : opt
    );
    onUpdate({ options: newOptions });
  };

  const addOption = () => {
    const newOption = {
      id: uuidv4(),
      value: `option_${Date.now()}`,
      label: 'New Option',
    };
    onUpdate({ options: [...field.options, newOption] });
  };

  const removeOption = (optionId) => {
    const newOptions = field.options.filter(opt => opt.id !== optionId);
    onUpdate({ options: newOptions });
  };

  const moveOption = (index, direction) => {
    const newOptions = [...field.options];
    const newIndex = index + direction;
    if (newIndex < 0 || newIndex >= newOptions.length) return;
    [newOptions[index], newOptions[newIndex]] = [newOptions[newIndex], newOptions[index]];
    onUpdate({ options: newOptions });
  };

  return (
    <div className="space-y-4">
      {/* Field Type Badge */}
      <div className="flex items-center justify-between">
        <span className="badge-primary capitalize">{field.type}</span>
        <button
          onClick={onRemove}
          className="btn-icon text-red-500 hover:bg-red-50"
          title="Remove field"
        >
          <Trash2 className="w-4 h-4" />
        </button>
      </div>

      {/* Basic Info */}
      <div className="space-y-3">
        <div>
          <label className="label">Label</label>
          <input
            type="text"
            value={field.label}
            onChange={handleLabelChange}
            className="input"
            placeholder="Enter field label"
          />
        </div>

        <div>
          <label className="label">Field Name</label>
          <input
            type="text"
            value={field.name}
            onChange={(e) => onUpdate({ name: e.target.value })}
            className="input"
            placeholder="field_name"
          />
          <p className="text-xs text-secondary-500 mt-1">
            Used for form submission data
          </p>
        </div>

        {(field.type === 'text' || field.type === 'email' || field.type === 'select') && (
          <div>
            <label className="label">Placeholder</label>
            <input
              type="text"
              value={field.placeholder || ''}
              onChange={(e) => onUpdate({ placeholder: e.target.value })}
              className="input"
              placeholder={field.type === 'select' ? 'Select an option...' : 'Enter text...'}
            />
          </div>
        )}

        <div>
          <label className="label">Help Text</label>
          <input
            type="text"
            value={field.helpText || ''}
            onChange={(e) => onUpdate({ helpText: e.target.value })}
            className="input"
            placeholder="Additional information for users"
          />
        </div>

        <div className="flex items-center justify-between py-2">
          <label className="label mb-0">Required Field</label>
          <button
            onClick={() => onUpdate({ required: !field.required })}
            className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
              field.required ? 'bg-primary-500' : 'bg-secondary-300'
            }`}
          >
            <span
              className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                field.required ? 'translate-x-6' : 'translate-x-1'
              }`}
            />
          </button>
        </div>
      </div>

      {/* Options for Select Field */}
      {field.type === 'select' && (
        <div className="border-t border-secondary-200 pt-4">
          <div className="flex items-center justify-between mb-3">
            <label className="label mb-0">Options</label>
            <button
              onClick={addOption}
              className="btn-ghost text-sm"
            >
              <Plus className="w-3 h-3 mr-1" />
              Add Option
            </button>
          </div>

          {field.options.length === 0 ? (
            <div className="text-center py-4 text-secondary-500 text-sm">
              No options yet. Click "Add Option" to create one.
            </div>
          ) : (
            <div className="space-y-2">
              {field.options.map((option, index) => (
                <div key={option.id} className="flex items-center space-x-2">
                  <GripVertical className="w-4 h-4 text-secondary-400 cursor-grab" />
                  <input
                    type="text"
                    value={option.label}
                    onChange={(e) => handleOptionChange(option.id, 'label', e.target.value)}
                    className="input flex-1"
                    placeholder="Option Label"
                  />
                  <input
                    type="text"
                    value={option.value}
                    onChange={(e) => handleOptionChange(option.id, 'value', e.target.value)}
                    className="input w-24"
                    placeholder="Value"
                  />
                  <button
                    onClick={() => moveOption(index, -1)}
                    disabled={index === 0}
                    className="btn-icon"
                  >
                    <ChevronUp className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => moveOption(index, 1)}
                    disabled={index === field.options.length - 1}
                    className="btn-icon"
                  >
                    <ChevronDown className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => removeOption(option.id)}
                    className="btn-icon text-red-500 hover:bg-red-50"
                  >
                    <X className="w-4 h-4" />
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Validation Settings */}
      <div className="border-t border-secondary-200 pt-4">
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="flex items-center justify-between w-full text-left"
        >
          <label className="label mb-0">Validation</label>
          {isExpanded ? (
            <ChevronUp className="w-4 h-4 text-secondary-500" />
          ) : (
            <ChevronDown className="w-4 h-4 text-secondary-500" />
          )}
        </button>

        {isExpanded && (
          <div className="space-y-3 mt-3">
            {field.type === 'text' && (
              <>
                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <label className="label text-xs">Min Length</label>
                    <input
                      type="number"
                      value={field.validation?.minLength || ''}
                      onChange={(e) => onUpdate({
                        validation: { ...field.validation, minLength: parseInt(e.target.value) || undefined }
                      })}
                      className="input"
                      min="0"
                      placeholder="None"
                    />
                  </div>
                  <div>
                    <label className="label text-xs">Max Length</label>
                    <input
                      type="number"
                      value={field.validation?.maxLength || ''}
                      onChange={(e) => onUpdate({
                        validation: { ...field.validation, maxLength: parseInt(e.target.value) || undefined }
                      })}
                      className="input"
                      min="0"
                      placeholder="None"
                    />
                  </div>
                </div>
              </>
            )}

            {field.type === 'select' && field.options.length > 0 && (
              <p className="text-xs text-secondary-500">
                Minimum {field.validation?.minSelect || 1} to {field.validation?.maxSelect || field.options.length} options can be selected
              </p>
            )}

            <div className="flex items-center space-x-2 text-xs text-secondary-500">
              <AlertCircle className="w-3 h-3" />
              <span>Validation rules apply in preview mode</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FieldEditor;
