import React, { useState } from 'react';
import {
  CheckCircle,
  AlertCircle,
  Check,
  Send,
  Sparkles,
  FileText
} from 'lucide-react';

const FormPreview = ({ title, description, fields, validateField, darkMode }) => {
  const [formData, setFormData] = useState({});
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (fieldName, value) => {
    setFormData(prev => ({ ...prev, [fieldName]: value }));

    // Clear error when user starts typing
    if (errors[fieldName]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[fieldName];
        return newErrors;
      });
    }
  };

  const handleBlur = (fieldName) => {
    setTouched(prev => ({ ...prev, [fieldName]: true }));

    const field = fields.find(f => f.name === fieldName);
    if (field) {
      const fieldErrors = validateField(field, formData[fieldName]);
      if (fieldErrors.length > 0) {
        setErrors(prev => ({ ...prev, [fieldName]: fieldErrors }));
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newErrors = {};
    const newTouched = {};

    // Validate all fields
    fields.forEach(field => {
      newTouched[field.name] = true;
      const fieldErrors = validateField(field, formData[field.name]);
      if (fieldErrors.length > 0) {
        newErrors[field.name] = fieldErrors;
      }
    });

    setTouched(newTouched);
    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      setSubmitted(true);
    }
  };

  const getFieldError = (fieldName) => {
    if (touched[fieldName] && errors[fieldName]) {
      return errors[fieldName][0];
    }
    return null;
  };

  const isFieldValid = (fieldName) => {
    return touched[fieldName] && !errors[fieldName] && formData[fieldName];
  };

  const renderField = (field) => {
    const hasError = !!getFieldError(field.name);
    const isValid = isFieldValid(field.name);

    const fieldClassName = `input transition-all duration-200 ${
      hasError 
        ? 'input-error border-red-500 focus:ring-red-500' 
        : isValid 
          ? 'border-green-500 focus:ring-green-500 dark:border-green-500' 
          : ''
    }`;

    switch (field.type) {
      case 'text':
      case 'email':
        return (
          <div className="relative">
            <input
              type={field.type}
              className={fieldClassName}
              placeholder={field.placeholder}
              value={formData[field.name] || ''}
              onChange={(e) => handleChange(field.name, e.target.value)}
              onBlur={() => handleBlur(field.name)}
            />
            {isValid && (
              <div className="absolute right-3 top-1/2 -translate-y-1/2">
                <Check className="w-5 h-5 text-green-500" />
              </div>
            )}
          </div>
        );

      case 'select':
        return (
          <div className="relative">
            <select
              className={`custom-select pr-10 ${hasError ? 'input-error' : ''}`}
              value={formData[field.name] || ''}
              onChange={(e) => handleChange(field.name, e.target.value)}
              onBlur={() => handleBlur(field.name)}
            >
              <option value="">{field.placeholder || 'Select an option'}</option>
              {field.options?.map(option => (
                <option key={option.id} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {isValid && (
              <div className="absolute right-10 top-1/2 -translate-y-1/2">
                <Check className="w-5 h-5 text-green-500" />
              </div>
            )}
          </div>
        );

      case 'checkbox':
        return (
          <div className="flex items-center space-x-3">
            <label className="flex items-center cursor-pointer group">
              <input
                type="checkbox"
                className="custom-checkbox"
                checked={formData[field.name] || false}
                onChange={(e) => handleChange(field.name, e.target.checked)}
              />
              <span className="ml-3 text-sm text-secondary-700 dark:text-secondary-200 group-hover:text-secondary-900 dark:group-hover:text-white transition-colors">
                {field.helpText || field.label}
              </span>
            </label>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      {/* Form Card */}
      <div className="card overflow-hidden">
        {/* Form Header */}
        <div className="p-8 border-b border-secondary-100 dark:border-dark-300 bg-gradient-to-br from-primary-50/80 to-white dark:from-primary-900/20 dark:to-dark-200 text-center relative overflow-hidden">
          {/* Decorative elements */}
          <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-br from-primary-200/30 to-transparent rounded-full -translate-y-1/2 translate-x-1/2"></div>
          <div className="absolute bottom-0 left-0 w-24 h-24 bg-gradient-to-tr from-primary-200/20 to-transparent rounded-full translate-y-1/2 -translate-x-1/2"></div>

          <div className="relative">
            <div className="w-16 h-16 bg-gradient-to-br from-primary-500 to-primary-600 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg shadow-primary-500/30">
              <FileText className="w-8 h-8 text-white" />
            </div>
            <h1 className="text-2xl font-bold text-secondary-900 dark:text-white mb-2">
              {title}
            </h1>
            {description && (
              <p className="text-secondary-600 dark:text-secondary-300 max-w-lg mx-auto">
                {description}
              </p>
            )}
          </div>
        </div>

        {/* Success Message */}
        {submitted && (
          <div className="p-6 bg-gradient-to-r from-green-50 to-green-100 dark:from-green-900/30 dark:to-green-800/20 border-b border-green-200 dark:border-green-800">
            <div className="flex items-center justify-center space-x-3 text-green-700 dark:text-green-300">
              <div className="w-12 h-12 bg-green-500 rounded-full flex items-center justify-center">
                <CheckCircle className="w-6 h-6 text-white" />
              </div>
              <div className="text-center">
                <span className="text-lg font-semibold block">Form submitted successfully!</span>
                <span className="text-sm text-green-600 dark:text-green-400">Your data has been received</span>
              </div>
            </div>
          </div>
        )}

        {/* Form Fields */}
        <form onSubmit={handleSubmit} className="p-8 space-y-6">
          {fields.length === 0 ? (
            <div className="text-center py-12">
              <div className="w-20 h-20 bg-secondary-100 dark:bg-dark-400 rounded-full flex items-center justify-center mx-auto mb-4">
                <FileText className="w-10 h-10 text-secondary-400" />
              </div>
              <p className="text-secondary-500 dark:text-secondary-400 mb-4">
                No fields in this form yet.
              </p>
              <p className="text-sm text-secondary-400 dark:text-secondary-500">
                Go back to the builder to add fields.
              </p>
            </div>
          ) : (
            <div className="space-y-6">
              {fields.map((field) => (
                <div key={field.id} className="space-y-2">
                  {field.type !== 'checkbox' && (
                    <label className="label flex items-center space-x-2">
                      <span className="font-medium text-secondary-900 dark:text-white">{field.label}</span>
                      {field.required && <span className="text-red-500">*</span>}
                      {isFieldValid(field.name) && (
                        <span className="flex items-center text-green-500 text-xs">
                          <Check className="w-3 h-3 mr-1" />
                          Valid
                        </span>
                      )}
                    </label>
                  )}

                  {renderField(field)}

                  {/* Error Message */}
                  {getFieldError(field.name) && (
                    <div className="flex items-center space-x-1 text-sm text-red-500 animate-fade-in">
                      <AlertCircle className="w-4 h-4 flex-shrink-0" />
                      <span>{getFieldError(field.name)}</span>
                    </div>
                  )}

                  {/* Help Text */}
                  {!getFieldError(field.name) && field.helpText && field.type !== 'checkbox' && (
                    <p className="text-xs text-secondary-500 dark:text-secondary-400 flex items-center">
                      <Sparkles className="w-3 h-3 mr-1" />
                      {field.helpText}
                    </p>
                  )}
                </div>
              ))}
            </div>
          )}

          {/* Submit Button */}
          {fields.length > 0 && (
            <div className="pt-4">
              <button
                type="submit"
                className="btn-primary w-full py-4 text-base font-semibold shadow-lg shadow-primary-500/20 hover:shadow-xl hover:shadow-primary-500/30 transform hover:-translate-y-0.5 transition-all duration-200"
              >
                <Send className="w-5 h-5 mr-2" />
                Submit Form
              </button>
            </div>
          )}
        </form>

        {/* Form Footer */}
        <div className="px-8 py-4 bg-secondary-50 dark:bg-dark-400 border-t border-secondary-100 dark:border-dark-300">
          <p className="text-xs text-secondary-500 dark:text-secondary-400 text-center flex items-center justify-center space-x-1">
            <Sparkles className="w-3 h-3" />
            <span>This is a preview of how your form will appear to users.</span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default FormPreview;
