import React, { useState } from 'react';
import {
  Eye,
  EyeOff,
  CheckCircle,
  AlertCircle,
  Check,
  ChevronDown
} from 'lucide-react';

const FormPreview = ({ title, description, fields, validateField }) => {
  const [formData, setFormData] = useState({});
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [showPasswords, setShowPasswords] = useState({});

  const handleChange = (fieldName, value) => {
    setFormData(prev => ({ ...prev, [fieldName]: value }));

    // Clear error when user starts typing
    if (errors[fieldName]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[fieldName];
        return newErrors;
      });
    }
  };

  const handleBlur = (fieldName) => {
    setTouched(prev => ({ ...prev, [fieldName]: true }));

    const field = fields.find(f => f.name === fieldName);
    if (field) {
      const fieldErrors = validateField(field, formData[fieldName]);
      if (fieldErrors.length > 0) {
        setErrors(prev => ({ ...prev, [fieldName]: fieldErrors }));
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newErrors = {};
    const newTouched = {};

    // Validate all fields
    fields.forEach(field => {
      newTouched[field.name] = true;
      const fieldErrors = validateField(field, formData[field.name]);
      if (fieldErrors.length > 0) {
        newErrors[field.name] = fieldErrors;
      }
    });

    setTouched(newTouched);
    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      setSubmitted(true);
    }
  };

  const getFieldError = (fieldName) => {
    if (touched[fieldName] && errors[fieldName]) {
      return errors[fieldName][0];
    }
    return null;
  };

  const isFieldValid = (fieldName) => {
    return touched[fieldName] && !errors[fieldName] && formData[fieldName];
  };

  const renderField = (field) => {
    const hasError = !!getFieldError(field.name);
    const isValid = isFieldValid(field.name);

    const fieldClassName = `input ${hasError ? 'input-error' : ''} ${
      isValid ? 'border-green-500 focus:ring-green-500' : ''
    }`;

    switch (field.type) {
      case 'text':
      case 'email':
        return (
          <input
            type={field.type}
            className={fieldClassName}
            placeholder={field.placeholder}
            value={formData[field.name] || ''}
            onChange={(e) => handleChange(field.name, e.target.value)}
            onBlur={() => handleBlur(field.name)}
          />
        );

      case 'select':
        return (
          <select
            className={`custom-select ${hasError ? 'input-error' : ''}`}
            value={formData[field.name] || ''}
            onChange={(e) => handleChange(field.name, e.target.value)}
            onBlur={() => handleBlur(field.name)}
          >
            <option value="">{field.placeholder || 'Select an option'}</option>
            {field.options?.map(option => (
              <option key={option.id} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        );

      case 'checkbox':
        return (
          <div className="flex items-center space-x-3">
            <label className="flex items-center cursor-pointer">
              <input
                type="checkbox"
                className="custom-checkbox"
                checked={formData[field.name] || false}
                onChange={(e) => handleChange(field.name, e.target.checked)}
              />
              <span className="ml-3 text-sm text-secondary-700">
                {field.helpText || field.label}
              </span>
            </label>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      {/* Form Card */}
      <div className="card">
        {/* Form Header */}
        <div className="p-8 border-b border-secondary-100 text-center bg-gradient-to-br from-primary-50 to-white">
          <h1 className="text-2xl font-bold text-secondary-900 mb-2">
            {title}
          </h1>
          {description && (
            <p className="text-secondary-600 max-w-lg mx-auto">
              {description}
            </p>
          )}
        </div>

        {/* Success Message */}
        {submitted && (
          <div className="p-4 bg-green-50 border-b border-green-200">
            <div className="flex items-center justify-center space-x-3 text-green-700">
              <CheckCircle className="w-6 h-6" />
              <span className="font-medium">Form submitted successfully!</span>
            </div>
          </div>
        )}

        {/* Form Fields */}
        <form onSubmit={handleSubmit} className="p-8 space-y-6">
          {fields.length === 0 ? (
            <div className="text-center py-8 text-secondary-500">
              No fields in this form yet. Go back to the builder to add fields.
            </div>
          ) : (
            fields.map((field) => (
              <div key={field.id} className="space-y-1">
                {field.type !== 'checkbox' && (
                  <label className="label flex items-center space-x-2">
                    <span>{field.label}</span>
                    {field.required && <span className="text-red-500">*</span>}
                    {isFieldValid(field.name) && (
                      <Check className="w-4 h-4 text-green-500" />
                    )}
                  </label>
                )}

                {renderField(field)}

                {/* Error Message */}
                {getFieldError(field.name) && (
                  <div className="flex items-center space-x-1 text-sm text-red-500 animate-fade-in">
                    <AlertCircle className="w-4 h-4 flex-shrink-0" />
                    <span>{getFieldError(field.name)}</span>
                  </div>
                )}

                {/* Help Text */}
                {!getFieldError(field.name) && field.helpText && field.type !== 'checkbox' && (
                  <p className="text-xs text-secondary-500">
                    {field.helpText}
                  </p>
                )}
              </div>
            ))
          )}

          {/* Submit Button */}
          {fields.length > 0 && (
            <div className="pt-4">
              <button
                type="submit"
                className="btn-primary w-full py-3 text-base"
              >
                <Check className="w-4 h-4 mr-2" />
                Submit Form
              </button>
            </div>
          )}
        </form>

        {/* Form Footer */}
        <div className="px-8 py-4 bg-secondary-50 border-t border-secondary-100 rounded-b-xl">
          <p className="text-xs text-secondary-500 text-center">
            This is a preview of how your form will appear to users.
          </p>
        </div>
      </div>
    </div>
  );
};

export default FormPreview;
