import React, { useState, useCallback } from 'react';
import { DragDropContext, Droppable } from '@hello-pangea/dnd';
import { v4 as uuidv4 } from 'uuid';
import { 
  Plus, 
  Type, 
  Mail, 
  List, 
  CheckSquare, 
  Eye, 
  Code, 
  Trash2, 
  GripVertical,
  Settings,
  X,
  Download,
  Upload,
  Check,
  AlertCircle
} from 'lucide-react';
import FieldEditor from './components/FieldEditor';
import FieldPreview from './components/FieldPreview';
import FormPreview from './components/FormPreview';
import JSONExport from './components/JSONExport';

// Field types configuration
const FIELD_TYPES = [
  { type: 'text', icon: Type, label: 'Text Field', description: 'Single line text input' },
  { type: 'email', icon: Mail, label: 'Email Field', description: 'Email address input' },
  { type: 'select', icon: List, label: 'Dropdown', description: 'Select from options' },
  { type: 'checkbox', icon: CheckSquare, label: 'Checkbox', description: 'Boolean selection' },
];

const initialFields = [
  {
    id: uuidv4(),
    type: 'text',
    label: 'Full Name',
    name: 'fullName',
    placeholder: 'Enter your full name',
    required: true,
    helpText: '',
    validation: { minLength: 2, maxLength: 50 },
  },
  {
    id: uuidv4(),
    type: 'email',
    label: 'Email Address',
    name: 'email',
    placeholder: 'Enter your email',
    required: true,
    helpText: 'We will never share your email',
    validation: {},
  },
  {
    id: uuidv4(),
    type: 'select',
    label: 'Country',
    name: 'country',
    placeholder: 'Select your country',
    required: true,
    options: [
      { id: uuidv4(), value: 'us', label: 'United States' },
      { id: uuidv4(), value: 'uk', label: 'United Kingdom' },
      { id: uuidv4(), value: 'ca', label: 'Canada' },
    ],
    helpText: '',
    validation: {},
  },
];

function App() {
  const [fields, setFields] = useState(initialFields);
  const [selectedField, setSelectedField] = useState(null);
  const [view, setView] = useState('builder'); // 'builder' | 'preview' | 'json'
  const [showFieldPicker, setShowFieldPicker] = useState(false);
  const [formTitle, setFormTitle] = useState('My Form');
  const [formDescription, setFormDescription] = useState('A sample form built with Form Builder');

  // Add new field
  const addField = useCallback((type) => {
    const newField = {
      id: uuidv4(),
      type,
      label: `New ${type.charAt(0).toUpperCase() + type.slice(1)} Field`,
      name: `field_${Date.now()}`,
      placeholder: '',
      required: false,
      helpText: '',
      options: type === 'select' ? [
        { id: uuidv4(), value: 'option1', label: 'Option 1' },
        { id: uuidv4(), value: 'option2', label: 'Option 2' },
      ] : [],
      validation: {},
    };

    setFields(prev => [...prev, newField]);
    setSelectedField(newField.id);
    setShowFieldPicker(false);
  }, []);

  // Update field
  const updateField = useCallback((id, updates) => {
    setFields(prev => prev.map(field => 
      field.id === id ? { ...field, ...updates } : field
    ));
  }, []);

  // Remove field
  const removeField = useCallback((id) => {
    setFields(prev => prev.filter(field => field.id !== id));
    if (selectedField === id) {
      setSelectedField(null);
    }
  }, [selectedField]);

  // Reorder fields
  const reorderFields = useCallback((result) => {
    if (!result.destination) return;

    const items = Array.from(fields);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setFields(items);
  }, [fields]);

  // Duplicate field
  const duplicateField = useCallback((id) => {
    const field = fields.find(f => f.id === id);
    if (!field) return;

    const newField = {
      ...field,
      id: uuidv4(),
      name: `${field.name}_copy`,
      label: `${field.label} (Copy)`,
    };

    const index = fields.findIndex(f => f.id === id);
    const newFields = [...fields];
    newFields.splice(index + 1, 0, newField);
    setFields(newFields);
    setSelectedField(newField.id);
  }, [fields]);

  // Export JSON
  const exportJSON = useCallback(() => {
    const schema = {
      title: formTitle,
      description: formDescription,
      fields: fields.map(({ id, ...field }) => field),
      metadata: {
        createdAt: new Date().toISOString(),
        version: '1.0',
      },
    };
    return JSON.stringify(schema, null, 2);
  }, [fields, formTitle, formDescription]);

  // Import JSON
  const importJSON = useCallback((jsonString) => {
    try {
      const schema = JSON.parse(jsonString);
      if (schema.fields && Array.isArray(schema.fields)) {
        setFields(schema.fields.map(field => ({ ...field, id: uuidv4() })));
        if (schema.title) setFormTitle(schema.title);
        if (schema.description) setFormDescription(schema.description);
        return true;
      }
      return false;
    } catch (e) {
      return false;
    }
  }, []);

  // Form validation
  const validateField = useCallback((field, value) => {
    const errors = [];

    if (field.required && !value) {
      errors.push(`${field.label} is required`);
    }

    if (field.validation?.minLength && value?.length < field.validation.minLength) {
      errors.push(`${field.label} must be at least ${field.validation.minLength} characters`);
    }

    if (field.validation?.maxLength && value?.length > field.validation.maxLength) {
      errors.push(`${field.label} must be no more than ${field.validation.maxLength} characters`);
    }

    if (field.type === 'email' && value && !/\S+@\S+\.\S+/.test(value)) {
      errors.push(`${field.label} must be a valid email`);
    }

    return errors;
  }, []);

  return (
    <div className="min-h-screen bg-secondary-50">
      {/* Header */}
      <header className="bg-white border-b border-secondary-200 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <div className="w-8 h-8 bg-primary-500 rounded-lg flex items-center justify-center">
                  <List className="w-5 h-5 text-white" />
                </div>
                <span className="text-xl font-semibold text-secondary-900">Form Builder</span>
              </div>
            </div>

            <div className="flex items-center space-x-3">
              {/* View Switcher */}
              <div className="flex items-center bg-secondary-100 rounded-lg p-1">
                <button
                  onClick={() => setView('builder')}
                  className={`flex items-center space-x-2 px-3 py-1.5 rounded-md text-sm font-medium transition-colors ${
                    view === 'builder' 
                      ? 'bg-white text-secondary-900 shadow-sm' 
                      : 'text-secondary-600 hover:text-secondary-900'
                  }`}
                >
                  <Settings className="w-4 h-4" />
                  <span className="hidden sm:inline">Builder</span>
                </button>
                <button
                  onClick={() => setView('preview')}
                  className={`flex items-center space-x-2 px-3 py-1.5 rounded-md text-sm font-medium transition-colors ${
                    view === 'preview' 
                      ? 'bg-white text-secondary-900 shadow-sm' 
                      : 'text-secondary-600 hover:text-secondary-900'
                  }`}
                >
                  <Eye className="w-4 h-4" />
                  <span className="hidden sm:inline">Preview</span>
                </button>
                <button
                  onClick={() => setView('json')}
                  className={`flex items-center space-x-2 px-3 py-1.5 rounded-md text-sm font-medium transition-colors ${
                    view === 'json' 
                      ? 'bg-white text-secondary-900 shadow-sm' 
                      : 'text-secondary-600 hover:text-secondary-900'
                  }`}
                >
                  <Code className="w-4 h-4" />
                  <span className="hidden sm:inline">JSON</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {view === 'builder' ? (
          <div className="flex flex-col lg:flex-row gap-8">
            {/* Field Picker Sidebar */}
            <div className="lg:w-80 flex-shrink-0">
              <div className="card p-4 sticky top-24">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-secondary-900">Add Fields</h2>
                  <span className="text-sm text-secondary-500">{fields.length} fields</span>
                </div>

                <button
                  onClick={() => setShowFieldPicker(!showFieldPicker)}
                  className="btn-primary w-full mb-4"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Add New Field
                </button>

                {showFieldPicker && (
                  <div className="space-y-2 mb-4 animate-fade-in">
                    {FIELD_TYPES.map(({ type, icon: Icon, label, description }) => (
                      <button
                        key={type}
                        onClick={() => addField(type)}
                        className="w-full flex items-center space-x-3 p-3 rounded-lg border border-secondary-200 hover:border-primary-300 hover:bg-primary-50 transition-all text-left group"
                      >
                        <div className="w-10 h-10 bg-secondary-100 rounded-lg flex items-center justify-center group-hover:bg-primary-100 transition-colors">
                          <Icon className="w-5 h-5 text-secondary-600 group-hover:text-primary-600" />
                        </div>
                        <div className="flex-1">
                          <p className="font-medium text-secondary-900">{label}</p>
                          <p className="text-xs text-secondary-500">{description}</p>
                        </div>
                        <Plus className="w-4 h-4 text-secondary-400 group-hover:text-primary-500" />
                      </button>
                    ))}
                  </div>
                )}

                {/* Form Info */}
                <div className="border-t border-secondary-200 pt-4">
                  <h3 className="text-sm font-medium text-secondary-900 mb-3">Form Details</h3>
                  <div className="space-y-3">
                    <div>
                      <label className="label">Form Title</label>
                      <input
                        type="text"
                        value={formTitle}
                        onChange={(e) => setFormTitle(e.target.value)}
                        className="input"
                      />
                    </div>
                    <div>
                      <label className="label">Description</label>
                      <textarea
                        value={formDescription}
                        onChange={(e) => setFormDescription(e.target.value)}
                        className="input resize-none"
                        rows={2}
                      />
                    </div>
                  </div>
                </div>

                {/* Actions */}
                <div className="border-t border-secondary-200 pt-4 mt-4">
                  <div className="grid grid-cols-2 gap-2">
                    <JSONExport 
                      data={{ title: formTitle, description: formDescription, fields }}
                      filename="form-schema.json"
                    />
                    <label className="btn-secondary cursor-pointer text-center">
                      <Upload className="w-4 h-4 mr-2" />
                      Import
                      <input
                        type="file"
                        accept=".json"
                        className="hidden"
                        onChange={(e) => {
                          const file = e.target.files?.[0];
                          if (file) {
                            const reader = new FileReader();
                            reader.onload = (event) => {
                              if (importJSON(event.target?.result)) {
                                alert('Form imported successfully!');
                              } else {
                                alert('Invalid JSON format');
                              }
                            };
                            reader.readAsText(file);
                          }
                        }}
                      />
                    </label>
                  </div>
                </div>
              </div>
            </div>

            {/* Builder Canvas */}
            <div className="flex-1">
              <div className="card">
                <div className="p-4 border-b border-secondary-200">
                  <h2 className="text-lg font-semibold text-secondary-900">Form Fields</h2>
                  <p className="text-sm text-secondary-500">Drag to reorder, click to edit</p>
                </div>

                <DragDropContext onDragEnd={reorderFields}>
                  <Droppable droppableId="fields">
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                        className={`p-4 min-h-[400px] ${
                          snapshot.isDraggingOver ? 'bg-primary-50' : ''
                        }`}
                      >
                        {fields.length === 0 ? (
                          <div className="flex flex-col items-center justify-center py-16 text-center">
                            <div className="w-16 h-16 bg-secondary-100 rounded-full flex items-center justify-center mb-4">
                              <Plus className="w-8 h-8 text-secondary-400" />
                            </div>
                            <h3 className="text-lg font-medium text-secondary-900 mb-1">No fields yet</h3>
                            <p className="text-secondary-500 mb-4">Click "Add New Field" to get started</p>
                            <button
                              onClick={() => addField('text')}
                              className="btn-primary"
                            >
                              <Plus className="w-4 h-4 mr-2" />
                              Add Text Field
                            </button>
                          </div>
                        ) : (
                          <div className="space-y-3">
                            {fields.map((field, index) => (
                              <div
                                key={field.id}
                                className={`field-enter ${
                                  selectedField === field.id ? 'ring-2 ring-primary-500' : ''
                                }`}
                                onClick={() => setSelectedField(field.id)}
                              >
                                <FieldPreview
                                  field={field}
                                  index={index}
                                  isSelected={selectedField === field.id}
                                  onRemove={() => removeField(field.id)}
                                  onDuplicate={() => duplicateField(field.id)}
                                />
                              </div>
                            ))}
                            {provided.placeholder}
                          </div>
                        )}
                      </div>
                    )}
                  </Droppable>
                </DragDropContext>
              </div>
            </div>

            {/* Field Editor Panel */}
            {selectedField && (
              <div className="lg:w-96 flex-shrink-0">
                <div className="card p-4 sticky top-24">
                  <div className="flex items-center justify-between mb-4">
                    <h2 className="text-lg font-semibold text-secondary-900">Edit Field</h2>
                    <button
                      onClick={() => setSelectedField(null)}
                      className="btn-icon"
                    >
                      <X className="w-4 h-4" />
                    </button>
                  </div>

                  <FieldEditor
                    field={fields.find(f => f.id === selectedField)}
                    onUpdate={(updates) => updateField(selectedField, updates)}
                    onRemove={() => removeField(selectedField)}
                  />
                </div>
              </div>
            )}
          </div>
        ) : view === 'preview' ? (
          <FormPreview
            title={formTitle}
            description={formDescription}
            fields={fields}
            validateField={validateField}
          />
        ) : (
          <div className="card">
            <div className="p-4 border-b border-secondary-200">
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-lg font-semibold text-secondary-900">Form Schema (JSON)</h2>
                  <p className="text-sm text-secondary-500">Export or copy this schema</p>
                </div>
                <JSONExport
                  data={{ title: formTitle, description: formDescription, fields }}
                  filename="form-schema.json"
                  showPreview={false}
                />
              </div>
            </div>
            <div className="p-4">
              <pre className="bg-secondary-900 text-secondary-100 p-4 rounded-lg overflow-auto max-h-[600px] text-sm">
                {exportJSON()}
              </pre>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
